function sleep(time) {
    let setTime = new Date().getTime() + time;
    while (true) {
        let now = new Date().getTime();
        if (now > setTime) {
            return;
        }
    }
}


function sleepSetT(time) {
    return new Promise((resolve) => setTimeout(resolve, time));
}



